function simplifyProduct(item) {
  return {
    target_product: (({ title, final_price, rating, url, image }) => ({
      title, final_price, rating, url, image
    }))(item.target_product),
    search_queries: item.search_queries.map(query => ({
      ...query,
      result: query.result.map(({ title, final_price, rating, url, image, rank }) => ({
        title, final_price, rating, url, image, rank
      }))
    }))
  };
}

// Get all 3 items from Merge
const mergedArray = $input.all().map(item => item.json);

// Convert into single object
const mergedObject = mergedArray.reduce((acc, obj) => {
  return { ...acc, ...obj };
}, {});

// Get workflow trigger input (from Search Analysis)
const triggerInput = $("Search Analysis").first().json;
const minimalInput = simplifyProduct(triggerInput);
// Merge trigger input + merged analysis
const finalOutput = {
  ...minimalInput,
  ...mergedObject
};

return finalOutput;

